import React, { Component } from "react";
import { Line } from "react-chartjs-2";
import axios from "axios";
import moment from "moment";

class aedchart extends Component {
  constructor(props) {
    super(props);
    this.state = {
      chartData: {},
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/currencies/AED")
      .then((response) => {
        var today = new Date();

        var Day = today.getDate();
        var Month = today.getMonth() + 1;
        var Month_str = Month.toString();
        var Year = today.getFullYear();

        if (Month_str.length === 1) {
          Month_str = "0" + Month_str;
        }

        var today_date = Day + "/" + Month_str + "/" + Year;

        var today_dbs = [];
        var today_uob = [];
        var today_ocbc = [];

        for (let i = 0, len = response.data.length; i < len; i++) {
          // let curr_date = response.data[i].Date;
          let db_dt =
            response.data[i].Date + " " + response.data[i].timeRecorded;
          let db_datetime = moment(db_dt, "DD-MM-YYYY HH:mm");
          let current_date = moment();
          let hour_difference = current_date.diff(db_datetime, "hours");
          if (hour_difference < 25) {
            if (response.data[i].Bank === "DBS") {
              today_dbs.push(response.data[i]);
            } else if (response.data[i].Bank === "UOB") {
              today_uob.push(response.data[i]);
            } else if (response.data[i].Bank === "OCBC") {
              today_ocbc.push(response.data[i]);
            }
          }
        }

        function get_rates(item) {
          var rate = item.rates[0].v / item.rates[1].v;
          return rate;
        }

        function get_timedate(item) {
          var timedate = item.timeRecorded;
          return timedate;
        }

        var dbs_rates = today_dbs.map(get_rates);
        var uob_rates = today_uob.map(get_rates);
        var ocbc_rates = today_ocbc.map(get_rates);

        var dbs_timedates = today_dbs.map(get_timedate);

        this.setState({
          chartData: {
            labels: dbs_timedates,
            datasets: [
              {
                label: "DBS",
                data: dbs_rates,
                backgroundColor: ["rgba(255, 255, 255, 0)"],
                borderColor: ["rgba(255, 159, 64, 1)"],
                borderWidth: 1,
              },
              {
                label: "UOB",
                data: uob_rates,
                backgroundColor: ["rgba(255, 255, 255, 0)"],
                borderColor: ["rgba(63, 191, 127, 1)"],
                borderWidth: 1,
              },
              {
                label: "OCBC",
                data: ocbc_rates,
                backgroundColor: ["rgba(255, 255, 255, 0)"],
                borderColor: ["rgba(191, 63, 63, 1)"],
                borderWidth: 1,
              },
            ],
          },
        });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    return (
      <div>
        <Line
          data={this.state.chartData}
          height="400"
          width="400"
          options={{ maintainAspectRatio: false }}
        />
      </div>
    );
  }
}

export default aedchart;
